<script>
document.addEventListener(\'DOMContentLoaded\', function() {
    // Анимация счетчиков
    const counters = document.querySelectorAll(\'.counter\');
    const speed = 200;
    
    const animateCounters = () => {
        counters.forEach(counter => {
            const target = parseInt(counter.getAttribute(\'data-target\'));
            const increment = target / speed;
            let current = 0;
            
            const updateCounter = () => {
                if (current < target) {
                    current += increment;
                    counter.textContent = Math.floor(current);
                    setTimeout(updateCounter, 1);
                } else {
                    counter.textContent = target;
                }
            };
            
            updateCounter();
        });
    };
    
    // Анимация кругов прогресса
    const circles = document.querySelectorAll(\'.circle-progress-path\');
    
    const animateCircles = () => {
        circles.forEach(circle => {
            const parent = circle.closest(\'.circle-progress\');
            const value = parseInt(parent.getAttribute(\'data-value\'));
            
            const circleValue = value > 100 ? 100 : value;
            const dasharray = `${circleValue}, 100`;
            
            setTimeout(() => {
                circle.style.strokeDasharray = dasharray;
            }, 300);
        });
    };
    
    // Функция проверки видимости элемента
    function isElementInViewport(el) {
        const rect = el.getBoundingClientRect();
        const windowHeight = window.innerHeight || document.documentElement.clientHeight;
        
        // Элемент виден, когда он находится в пределах 85% окна просмотра
        return (
            rect.top <= windowHeight * 0.85 &&
            rect.bottom >= windowHeight * 0.15
        );
    }
    
    // Обработчик скролла для показа секций
    function handleScroll() {
        const sections = document.querySelectorAll(\'section\');
        
        sections.forEach(section => {
            if (isElementInViewport(section) && !section.classList.contains(\'visible\')) {
                section.classList.add(\'visible\');
                
                // Запускаем анимацию счетчиков для секции с цифрами
                if (section.id === \'numbers\') {
                    setTimeout(() => {
                        animateCounters();
                        animateCircles();
                    }, 300);
                }
            }
        });
    }
    
    // Инициализация при загрузке
    function initScrollAnimations() {
        // Проверяем сразу видимые секции
        const sections = document.querySelectorAll(\'section\');
        sections.forEach(section => {
            if (isElementInViewport(section)) {
                section.classList.add(\'visible\');
                
                if (section.id === \'numbers\') {
                    setTimeout(() => {
                        animateCounters();
                        animateCircles();
                    }, 300);
                }
            }
        });
        
        // Добавляем обработчик скролла
        window.addEventListener(\'scroll\', handleScroll);
        
        // Для оптимизации используем requestAnimationFrame
        let ticking = false;
        const optimizedScroll = () => {
            if (!ticking) {
                ticking = true;
                requestAnimationFrame(() => {
                    handleScroll();
                    ticking = false;
                });
            }
        };
        
        window.addEventListener(\'scroll\', optimizedScroll);
    }
    
    // Инициализация
    initScrollAnimations();
    
    // Дополнительная проверка при полной загрузке
    window.addEventListener(\'load\', function() {
        // Проверяем первую секцию еще раз
        const firstSection = document.querySelector(\'.hero-utp\');
        if (firstSection && !firstSection.classList.contains(\'visible\')) {
            firstSection.classList.add(\'visible\');
        }
    });
    
    // Плавная прокрутка для ссылок
    document.querySelectorAll(\'a[href^="#"]\').forEach(anchor => {
        anchor.addEventListener(\'click\', function(e) {
            const targetId = this.getAttribute(\'href\');
            if (targetId === \'#\') return;
            
            const targetElement = document.querySelector(targetId);
            if (targetElement) {
                e.preventDefault();
                window.scrollTo({
                    top: targetElement.offsetTop - 80,
                    behavior: \'smooth\'
                });
            }
        });
    });
});
</script></p>
    
    
