    margin: 0 auto;
}

/* Центральный объект */
.scheme-building {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 3;
    width: 100px; /* Уменьшен размер */
    height: 100px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(255, 255, 255, 0.9);
    border-radius: 20px;
    border: 1px solid rgba(226, 232, 240, 0.8);
