    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

.scheme-building:hover {
    transform: translate(-50%, -50%) scale(1.05);
    box-shadow: 
        0 12px 40px rgba(0, 0, 0, 0.12),
        inset 0 1px 0 rgba(255, 255, 255, 0.9);
}

.building-icon {
