            const spans = mobileBtn.querySelectorAll(\'span\');
            spans[0].style.transform = \'rotate(45deg) translate(7px, 7px)\';
            spans[1].style.opacity = \'0\';
            spans[2].style.transform = \'rotate(-45deg) translate(7px, -7px)\';
            
