    const dropdownItems = document.querySelectorAll(\'.has-dropdown\');
    
    // Глобальные переменные для управления меню
    let isMobileMenuOpen = false;
    let scrollPosition = 0;
    
    // ========== 1. МОБИЛЬНОЕ МЕНЮ ==========
    if (mobileBtn && navMenu && overlay) {
